import React from 'react';
import ReactDOM from 'react-dom';
import logo from './img/iconfinder_Star_5349757.png';
import trigger from './img/iconfinder_basics-12_296823.png';

const MoreRow = props =>
  <div className="row" id="more">
    <div className="box1 col">
    <h2>Box 5</h2>
    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec pretium ante erat, vitae sodales mi varius quis. Etiam vestibulum lorem vel urna tempor, eu fermentum odio aliquam. Aliquam consequat urna vitae ipsum pulvinar, in blandit purus eleifend.</p></div>
    <div className="box2 col">
    <h2>Box 6</h2>
    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec pretium ante erat, vitae sodales mi varius quis. Etiam vestibulum lorem vel urna tempor, eu fermentum odio aliquam. Aliquam consequat urna vitae ipsum pulvinar, in blandit purus eleifend.</p></div>
    <div className="box3 col">
    <h2>Box 7</h2>
    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec pretium ante erat, vitae sodales mi varius quis. Etiam vestibulum lorem vel urna tempor, eu fermentum odio aliquam. Aliquam consequat urna vitae ipsum pulvinar, in blandit purus eleifend.</p></div>
    <div className="box4 col">
    <h2>Box 8</h2>
    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec pretium ante erat, vitae sodales mi varius quis. Etiam vestibulum lorem vel urna tempor, eu fermentum odio aliquam. Aliquam consequat urna vitae ipsum pulvinar, in blandit purus eleifend.</p></div>
  </div>

// const Square = props =>
//   <div className="m-test" style={{display: this.state.display}}></div>
  // const MoreRow2 = props =>
  //   <div>
  //     {MoreRow.filter(row => !props.isRowShow)}
  //   </div>

class App extends React.Component {
  // construstor(props){
  //   super(props);
  //   this.state = {
  //     text: "Welcome Message",
  //     isRowShow: true,

  //     isToggleOn: true,
  //     dispaly: 'block'
  //   }
  // }
  state = {
    text: "Welcome Message",
    isRowShow: true,

    isToggleOn: true,
    dispaly: 'block'
  }
  handleClick = this.handleClick.bind(this);

  rowFilter = () =>
    this.setState({ isRowShow: !this.state.isRowShow });
  changeText = () => {
    this.setState({text: "Have a Good Time!"});
  }

  handleClick() {
    this.setState(prevState => ({
      isToggleOn: !prevState.isToggleOn,
      display: prevState.isToggleOn ? 'none': 'block'
    }));
  }

  render(){
    return (

      <div>
        <div className="main-header">
          <div className="container">
            <h1 className="name"><button>Grace<img className="logo" src={logo} alt="logo" /></button></h1>
            <ul className="main-nav">
              <li className="menuTrigger" id="trigger"><img src={trigger} alt="trigger" /></li>
              <li><button>Portfolio</button></li>
              <li><button>Experience</button></li>
              <li><button>Skills</button></li>
              <li><button>Contact</button></li>   
            </ul>
          </div>
        </div>
        <div className="list" id="listDiv">
          <button className="X" id="toggleList">X</button>
          <ul>
            <li>Portfolio</li>
            <li>Experience</li>
            <li>Skills</li>
            <li>Contact</li>
          </ul>
        </div>
        {/* /.main-header */}
        <div className="banner"><div onClick={this.changeText} className="wel" id="welcome">{this.state.text}</div></div>
        <div className="secTitle">Section Title</div>
        <div className="row">
          <div className="box1 col">
            <h2>Box 1 </h2>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec pretium ante erat, vitae sodales mi varius quis. Etiam vestibulum lorem vel urna tempor, eu fermentum odio aliquam. Aliquam consequat urna vitae ipsum pulvinar, in blandit purus eleifend.</p></div>
          <div className="box2 col">
            <h2>Box 2</h2>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec pretium ante erat, vitae sodales mi varius quis. Etiam vestibulum lorem vel urna tempor, eu fermentum odio aliquam. Aliquam consequat urna vitae ipsum pulvinar, in blandit purus eleifend.</p></div>
          <div className="box3 col">
            <h2>Box 3</h2>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec pretium ante erat, vitae sodales mi varius quis. Etiam vestibulum lorem vel urna tempor, eu fermentum odio aliquam. Aliquam consequat urna vitae ipsum pulvinar, in blandit purus eleifend.</p></div>
          <div className="box4 col">
            <h2>Box 4</h2>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec pretium ante erat, vitae sodales mi varius quis. Etiam vestibulum lorem vel urna tempor, eu fermentum odio aliquam. Aliquam consequat urna vitae ipsum pulvinar, in blandit purus eleifend.</p></div>
        </div>
        <div className="btn" id="callAction">
          <button onClick={this.rowFilter} checked={this.state.isRowShow}>call to action</button>
        </div>
        <MoreRow isRowShow={this.state.isRowShow}/>
        <div>
          <button onClick={this.handleClick}>
            {this.state.isToggleOn ? 'ON' : 'OFF'}
          </button>
        {/* <Square isToggleOn={this.state.isToggleOn} dispaly={this.state.dispaly}/> */}
          <div className="m-test" style={{display: this.state.display}}></div>
        </div>
    </div>

    );
  }
}
ReactDOM.render(
  <App />,
  document.getElementById('root') 
);


export default App;
